const rawText = $input.first().json.candidates[0].content.parts[0].text;
const fileName = $("Webhook").first().binary.audio_file.fileName;
const fecha = new Date().toISOString();

const lines = rawText
  .split('\n')
  .map(line => line.trim())
  .filter(line => line.startsWith('###|'));

const textoLegible = rawText.replace(/\\n/g, '\n').trim();

function normalizarTimestamp(raw) {
  try {
    const partes = raw.split(':');
    let horas = '00', minutos = '00', segundos = '00', milis = '000';

    if (partes.length === 1) {
      segundos = partes[0].padStart(2, '0');
    } else if (partes.length === 2) {
      minutos = partes[0].padStart(2, '0');
      const segundoPartes = partes[1].split('.');
      segundos = segundoPartes[0].padStart(2, '0');
      if (segundoPartes[1]) milis = segundoPartes[1].padEnd(3, '0');
    } else if (partes.length === 3) {
      horas = partes[0].padStart(2, '0');
      minutos = partes[1].padStart(2, '0');
      const segundoPartes = partes[2].split('.');
      segundos = segundoPartes[0].padStart(2, '0');
      if (segundoPartes[1]) milis = segundoPartes[1].padEnd(3, '0');
    } else {
      return raw;
    }

    return `${horas}:${minutos}:${segundos}.${milis}`;
  } catch (error) {
    return raw;
  }
}

const hablantesSet = new Set();
const puntos = [];

const transcripcion = lines.map((line, index) => {
  const regex = /^###\|(\d+)\|([\d:.]+)\|(.+?)\|(.*)$/;
  const match = line.match(regex);

  if (match) {
    const linea = parseInt(match[1], 10);
    const timestamp = normalizarTimestamp(match[2]);
    const hablante = match[3].trim();
    const texto = match[4].trim();

    hablantesSet.add(hablante);

    puntos.push({
      id: `${fileName}-linea-${String(linea).padStart(3, '0')}`,
      vector: [], // se llenarÃ¡ despuÃ©s
      payload: {
        archivo: fileName,
        fecha,
        hablantes: [], // se llenarÃ¡ al final con los valores Ãºnicos
        linea,
        timestamp,
        hablante,
        texto,
        audio: fileName
      }
    });

    return {
      linea,
      timestamp,
      hablante,
      texto,
      audio: fileName
    };
  } else {
    return {
      error: "Formato no reconocido",
      contenido: line,
      index
    };
  }
});

// Agregar lista de hablantes Ãºnicos a cada punto
const hablantesArray = Array.from(hablantesSet);
puntos.forEach(p => {
  p.payload.hablantes = hablantesArray;
});

// Resultado final
return [{
  json: {
    data: {
      transcripcion: puntos,
      textoLegible,
    }
  }
}];

